package com.test.itextpdf;

import org.junit.Test;
import sun.misc.BASE64Decoder;
import sun.misc.BASE64Encoder;

import java.io.FileInputStream;
import java.io.IOException;
import java.io.InputStream;
import java.nio.file.Files;
import java.nio.file.Paths;

public class Base64Utils {
    /**
     * 图片转化成 base64 字符串
     * 将图片文件转化为字节数组字符串，并对其进行 base64 编码处理
     */
    public static String getImageStr(String imgPath) {
        byte[] data;
        // 返回Base64编码过的字节数组字符串
        String encode;
        // 对字节数组Base64编码
        BASE64Encoder encoder = new BASE64Encoder();
        try (InputStream in = new FileInputStream(imgPath)) {
            data = new byte[in.available()];
            in.read(data);
            encode = encoder.encode(data);
        } catch (IOException e) {
            return null;
        }
        return encode;
    }

    /**
     * png文件对应的 base64 字符串解码
     */
    public static byte[] decodeBase64Png(String imgData) {

        // 图像数据为空
        if (imgData == null || imgData.trim().equals("") || !imgData.contains("data:image/png;base64,"))
            return null;

        imgData = imgData.replace("data:image/png;base64,", "");
        try {
            byte[] bytes = new BASE64Decoder().decodeBuffer(imgData);
            for (int i = 0; i < bytes.length; i++) {
                if (bytes[i] < 0) {
                    bytes[i] += 256;
                }
            }
            return bytes;
        } catch (IOException e) {
            return null;
        }
    }

    /**
     * byte[] 保存为图片
     * @param filePath 文件保存路径
     * @param bytes 文件字节
     */
    public static void saveFileFromBytes(String filePath, byte[] bytes) {
        try {
            Files.write(Paths.get(filePath), bytes);
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    @Test
    public void Test() {

        byte[] bytes = decodeBase64Png("data:image/png;base64,");
        saveFileFromBytes("C:\\Users\\admin\\Desktop\\temp\\output\\sss.png", bytes);
    }
}